mapper.select:
SELECT CATE_NO, CATE_NAME 
FROM CATEGORY
WHERE TRUNC(CATE_NO,-2) =#{categoryNo}
AND NOT CATE_NO= #{categoryNo}

mapper.insert:
		
		INSERT INTO PRODUCT(
                    PRO_NO
                    ,PRO_TITLE
                    ,PRO_CONTENT
                    ,SELL_ID
                    ,PRO_PRICE
                    ,WISH_PRICE
                    ,POST_PRICE
                    ,START_DATE
                    ,END_DATE
                    ,PRO_CATE_NO
                    ,TRADE_CATE_NO
                    ,TRADE_LOCATION
                    )
            VALUES(
                    SEQ_PRNO.NEXTVAL
                    ,#{proTitle}
                    ,#{proContent}
                    ,#{sellId}
                    ,#{proPrice}
                    ,#{wishPrice}
                    ,#{postPrice}
                    ,SYSDATE
                    ,SYSDATE+#{endDate}
                    ,#{proCateNo}
                    ,#{tradeCateNo}
                    ,#{tradeLocation}
                  )
