mapper.select:
SELECT CATE_NO, CATE_NAME 
FROM CATEGORY
WHERE TRUNC(CATE_NO,-2) =#{cateNo}
AND NOT CATE_NO= #{cateNo}

SELECT IMAGE_NO, PRO_NO, ORINAME, SYSNAME, PATH
FROM PRODUCT_IMAGES
WHERE PRO_NO =#{pNo}

mapper.insert:
		
		INSERT INTO PRODUCT(
                    PRO_NO
                    ,PRO_TITLE
                    ,PRO_CONTENT
                    ,SELL_ID
                    ,PRO_PRICE
                    ,WISH_PRICE
                    ,POST_PRICE
                    ,START_DATE
                    ,END_DATE
                    ,PRO_CATE_NO
                    ,TRADE_CATE_NO
                    ,TRADE_LOCATION
                    )
            VALUES(
                    SEQ_PRNO.NEXTVAL
                    ,#{proTitle}
                    ,#{proContent}
                    ,#{sellId}
                    ,#{proPrice}
                    ,#{wishPrice}
                    ,#{postPrice}
                    ,SYSDATE
 					,SYSDATE+#{bidDate}
                    ,#{proCateNo}
                    ,#{tradeCateNo}
                    ,#{tradeLocation}
                  )

insert into PRODUCT_IMAGES( IMAGE_NO ,PRO_NO ,ORINAME ,SYSNAME ,PATH ) SELECT SEQ_IMGNO.NEXTVAL,SEQ_PRNO.CURRVAL, A.* from(
select #{item.oriName} as ORINAME, #{item.sysName} as SYSNAME, #{item.path} as PATH from dual
) A

mapper.select:
SELECT COUNT(*)
FROM PRO_LIKE
WHERE USER_ID = #{userId}

SELECT COUNT(*)
FROM PRO_LIKE
WHERE USER_ID = #{userId}
AND PRO_NO = #{pNo}

mapper.update:
UPDATE PRODUCT
SET PRO_STATUS='S'
WHERE PRO_NO = #{ch}

UPDATE POSTBOX
SET PBOX_NAME='바보'
WHERE PBOX_NO = #{ch2}

UPDATE BID
SET BID_STATUS='S'
WHERE BID_NO = #{ch3}

UPDATE POSTBOX
SET PBOX_NAME='바보'
WHERE PBOX_NO = #{ch4}
