mapper.select:
SELECT CATE_NO, CATE_NAME 
FROM CATEGORY
WHERE TRUNC(CATE_NO,-2) =#{cateNo}
AND NOT CATE_NO= #{cateNo}

mapper.insert:
		
		INSERT INTO PRODUCT(
                    PRO_NO
                    ,PRO_TITLE
                    ,PRO_CONTENT
                    ,SELL_ID
                    ,PRO_PRICE
                    ,WISH_PRICE
                    ,POST_PRICE
                    ,START_DATE
                    ,END_DATE
                    ,PRO_CATE_NO
                    ,TRADE_CATE_NO
                    ,TRADE_LOCATION
                    )
            VALUES(
                    SEQ_PRNO.NEXTVAL
                    ,#{proTitle}
                    ,#{proContent}
                    ,#{sellId}
                    ,#{proPrice}
                    ,#{wishPrice}
                    ,#{postPrice}
                    ,SYSDATE
                    ,TO_DATE(SYSDATE+#{bidDate},'YYYY-MM-DD HH24:MI:SS')
                    ,#{proCateNo}
                    ,#{tradeCateNo}
                    ,#{tradeLocation}
                  )

	INSERT INTO PRODUCT_IMAGES(
												IMAGE_NO
												,PRO_NO
												,ORINAME
												,SYSNAME
												,PATH
												)
							VALUES
						
												(
												 SEQ_IMGNO.NEXTVAL
												,SEQ_PRNO.CURVAL

												,#{oriName[index]}


												,#{sysName[index]}


												,#{path[index]}

												)
